values('55','ajax上传文件','通过ajax方式上传文件，使用FromData进行Ajax请求','<p>&lt;!DOCTYPE html&gt;</p><p>&lt;html&gt;</p><p>&lt;head&gt;</p><p>&lt;meta charset=&quot;UTF-8&quot;&gt;</p><p>&lt;title&gt;&lt;/title&gt;</p><p>&lt;/head&gt;</p><p>&lt;body&gt;</p><p>&lt;center&gt;</p><p>&lt;form id= &quot;uploadForm&quot;&gt; &nbsp;</p><p>&nbsp; &nbsp; &nbsp;上传文件:&amp;nbsp;&amp;nbsp; &lt;input type=&quot;file&quot; id=&quot;file&quot; name=&quot;file&quot; multiple=&quot;true&quot;&gt;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &lt;input type=&quot;button&quot; value=&quot;多文件上传&quot; onclick=&quot;xhr2()&quot;/&gt;</p><p>&lt;/form&gt; &nbsp;</p><p>&lt;/center&gt;</p><p>&lt;/body&gt;</p><p>&lt;/html&gt;</p><p>&lt;script&gt;</p><p>function xhr2(){</p><p>&nbsp; &nbsp; var file = document.getElementById(&#39;file&#39;).files;</p><p>&nbsp; &nbsp; // var img_desc = document.getElementsByName(&#39;img_desc&#39;)[0].value;</p><p>&nbsp; &nbsp; //console.log(img_desc);</p><p>&nbsp; &nbsp; var xhr = new XMLHttpRequest();//第一步</p><p>&nbsp; &nbsp; xhr.open(&#39;POST&#39;,&#39;111.php&#39;);//第二步</p><p>&nbsp; &nbsp; //发送请求</p><p>&nbsp; &nbsp; var formData = new FormData();</p><p>&nbsp; &nbsp; console.log(formData);</p><p>&nbsp; &nbsp;</p><p>&nbsp; &nbsp; for(var i=0;i&lt;file.length;i++){</p><p>&nbsp; &nbsp; &nbsp; formData.append(&#39;name[]&#39;,file[i]);</p><p>&nbsp; &nbsp; }</p><p>&nbsp; &nbsp; &nbsp;// formData.append(&#39;img_desc&#39;,img_desc);</p><p>&nbsp; &nbsp; xhr.send(formData);//第三步骤</p><p>&nbsp; &nbsp; //ajax返回</p><p>&nbsp; &nbsp; xhr.onreadystatechange = function(){//第四步</p><p>&nbsp; &nbsp; &nbsp; if(xhr.readyState == 4 &amp;&amp; xhr.status == 200){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; console.log(xhr.responseText);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; // if(xhr.responseText == 1){</p><p>&nbsp; &nbsp; &nbsp; &nbsp; // &nbsp; &nbsp; alert(&quot;上传成功&quot;);</p><p>&nbsp; &nbsp; &nbsp; &nbsp; // }else{</p><p>&nbsp; &nbsp; &nbsp; &nbsp; // &nbsp; &nbsp; alert(&quot;上传失败&quot;);</p>