e&#39;] = $path;//添加入库<br/>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;// print_r($_POST[&#39;file&#39;]);die;<br/>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br/>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;}<br/></p>','秦林慧','2016-09-19 08:10:28','7','10','./Public/Uploads/2016-09-19/57df2cf497b5f.jpg');
