values('46','php计算几分钟前、几小时前、几天前的几个函数、类分享','计算几分钟前、几小时前、几天前的几个函数','<p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">一、函数实现<br/>实例1：<br/></p><p><span style="float: right; cursor: pointer;"><span style="text-decoration:underline;">.</span></span>代码如下:</p><p><br/>function time_tran($the_time){<br/>&nbsp;&nbsp; $now_time = date(&quot;Y-m-d H:i:s&quot;,time()+8*60*60);<br/>&nbsp;&nbsp; $now_time = strtotime($now_time);<br/>&nbsp;&nbsp; $show_time = strtotime($the_time);<br/>&nbsp;&nbsp; $dur = $now_time - $show_time;<br/>&nbsp;&nbsp; if($dur &lt; 0){<br/>&nbsp;&nbsp;&nbsp; return $the_time;<br/>&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp; if($dur &lt; 60){<br/>&nbsp;&nbsp;&nbsp;&nbsp; return $dur.&#39;秒前&#39;;<br/>&nbsp;&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp;&nbsp; if($dur &lt; 3600){<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return floor($dur/60).&#39;分钟前&#39;;<br/>&nbsp;&nbsp;&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($dur &lt; 86400){<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return floor($dur/3600).&#39;小时前&#39;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($dur &lt; 259200){//3天内<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return floor($dur/86400).&#39;天前&#39;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $the_time;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br/>&nbsp;}<br/></p><p><br/>实例2：<br/></p><p><span style="float: right; cursor: pointer;"><span style="text-decoration:underline;">.</span></span>代码如下:</p><p>&lt;?php<br/>function format_date($time){<br/>&nbsp;&nbsp;&nbsp; $t=time()-$time;<br/>&nbsp;&nbsp;&nbsp; $f=array(<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;31536000&#39;=&gt;&#39;年&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;2592000&#39;=&gt;&#39;个月&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;604800&#39;=&gt;&#39;星期&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;86400&#39;=&gt;&#39;天&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;3600&#39;=&gt;&#39;小时&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;60&#39;=&gt;&#39;分钟&#39;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &#39;1&#39;=&gt;&#39;秒&#39;<br/>&nbsp;&nbsp;&nbsp; );<br/>&nbsp;&nbsp;&nbsp; foreach ($f as $k=&gt;$v)&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (0 !=$c=floor($t/(int)$k)) {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $c.$v.&#39;前&#39;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br/>&nbsp;&nbsp;&nbsp; }<br/>}<br/>?&gt;</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;"><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">实例3：<br/></p><p><span style="float: right; cursor: pointer;"><span style="text-decoration:underline;">.</span></span>代码如下:</p><p><br/>function formatTime($date) {<br/>$str = &#39;&#39;;<br/>$timer = strtotime($date);<br/>$diff = $_SERVER[&#39;REQUEST_TIME&#39;] - $timer;<br/>$day = floor($diff / 86400);<br/>$free = $diff % 86400;<br/>if($day &gt; 0) {<br/>return $day.&quot;天前&quot;;<br/>}else{<br/>if($free&gt;0){<br/>$hour = floor($free / 3600);<br/>$free = $free % 3600;<br/>if($hour&gt;0){<br/>return $hour.&quot;小时前&quot;;<br/>}else{<br/>if($free&gt;0){<br/>$min = floor($free / 60);<br/>$free = $free % 60;<br/>if($min&gt;0){<br/>return $min.&quot;分钟前&quot;;<br/>}else{<br/>if($free&gt;0){<br/>return $free.&quot;秒前&quot;;<br/>}else{<br/>return &#39;刚刚&#39;;<br/>}<br/>}<br/>}else{<br/>return &#39;刚刚&#39;;<br/>}<br/>}<br/>}else{<br/>return &#39;刚刚&#39;;<br/>}<br/>}<br/>}<br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;"><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">实例4：<br/></p><p><span style="float: right; cursor: pointer;"><span style="text-decoration:underline;">.</span></span>代码如下:</p><p><br/>function time_tran($the_time){<br/>$now_time = date(&quot;Y-m-d H:i:s&quot;,time()+8*60*60);&nbsp;<br/>$now_time = strtotime($now_time);<br/>$show_time = strtotime($the_time);<br/>$dur = $now_time - $show_time;<br/>if($dur &lt; 0){<br/>return $the_time;&nbsp;<br/>}else{<br/>if($dur &lt; 60){<br/>&nbsp;&nbsp;&nbsp; return $dur.&#39;秒前&#39;;&nbsp;<br/>}else{<br/>&nbsp;&nbsp;&nbsp; if($dur &lt; 3600){<br/>&nbsp;&nbsp; return floor($dur/60).&#39;分钟前&#39;;&nbsp;<br/>&nbsp;&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp; if($dur &lt; 86400){<br/>&nbsp;&nbsp; return floor($dur/3600).&#39;小时前&#39;;&nbsp;<br/>&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp; if($dur &lt; 259200){//3天内<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return floor($dur/86400).&#39;天前&#39;;<br/>&nbsp;&nbsp; }else{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $the_time;&nbsp;<br/>&nbsp;&nbsp; }<br/>&nbsp;&nbsp; }<br/>&nbsp;&nbsp;&nbsp; }<br/>}<br/>}<br/>}<br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;"><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">二、类的实现<br/></p><p><span style="float: right; cursor: pointer;"><span style="text-decoration:underline;">.</span></span>代码如下:</p><p>&lt;?php<br/>/*<br/>&nbsp;* author: Solon Ring<br/>&nbsp;* time: 2011-11-02<br/>&nbsp;* 发博时间计算(年，月，日，时，分，秒)<br/>&nbsp;* $createtime 可以是当前时间<br/>&nbsp;* $gettime 你要传进来的时间<br/>&nbsp;*/</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;"><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">class Mygettime{</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function&nbsp; __construct($createtime,$gettime) {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;createtime = $createtime;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $this-&gt;gettime = $gettime;<br/>&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp; function getSeconds()<br/>&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return $this-&gt;createtime-$this-&gt;gettime;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp; function getMinutes()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ($this-&gt;createtime-$this-&gt;gettime)/(60);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getHours()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ($this-&gt;createtime-$this-&gt;gettime)/(60*60);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getDay()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ($this-&gt;createtime-$this-&gt;gettime)/(60*60*24);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getMonth()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ($this-&gt;createtime-$this-&gt;gettime)/(60*60*24*30);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function getYear()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return ($this-&gt;createtime-$this-&gt;gettime)/(60*60*24*30*12);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; function index()<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getYear() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getYear() &gt; 2)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return date(&quot;Y-m-d&quot;,$this-&gt;gettime);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getYear()).&quot; 年前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getMonth() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getMonth()).&quot; 月前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getDay() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getDay()).&quot; 天前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getHours() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getHours()).&quot; 小时前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getMinutes() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getMinutes()).&quot; 分钟前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if($this-&gt;getSeconds() &gt; 1)<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return intval($this-&gt;getSeconds()-1).&quot; 秒前&quot;;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exit();<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;">&nbsp; }<br/>//类的使用实例<br/>/*<br/>&nbsp;*<br/>&nbsp;* 调用类输出方式<br/>&nbsp;*<br/>&nbsp;* $a = new Mygettime(time(),strtotime(&#39;-25 month&#39;));<br/>&nbsp;* echo iconv(&#39;utf-8&#39;, &#39;gb2312&#39;, $a-&gt;index())?iconv(&#39;utf-8&#39;, &#39;gb2312&#39;, $a-&gt;index()):iconv(&#39;utf-8&#39;, &#39;gb2312&#39;, &#39;当前&#39;);<br/>&nbsp;*<br/>&nbsp;*/<br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none;"><br/></p><p><br/></p>','秦林慧','2016-09-14 11:05:34','7','5','./Public/Uploads/2016-09-14/57d8be7ed1bcd.jpg');
