values('44','复选框获取遍历id','复选框获取遍历id','<p><br/></p><p>&lt;script type=&quot;text/javascript&quot; src=&quot;jquery.1.12.js&quot;&gt;&lt;/script&gt;</p><p>&lt;script&gt;</p><p><span class="Apple-tab-span" style="white-space:pre">	</span>$(function(){&nbsp;</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp;$(&quot;#getadd&quot;).click(function() {</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp; &nbsp; &nbsp;text = $(&quot;input:checkbox[name=&#39;box[]&#39;]:checked&quot;).map(function(index,elem) {</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;return $(elem).val();</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp; &nbsp; &nbsp;}).get().join(&#39;,&#39;);</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp; &nbsp; &nbsp;alert(&quot;选中的checkbox的值为：&quot;+text);</p><p><span class="Apple-tab-span" style="white-space:pre">	</span> &nbsp; &nbsp;});</p><p><span class="Apple-tab-span" style="white-space:pre">	</span>});<span class="Apple-tab-span" style="white-space:pre">	</span></p><p>&lt;/script&gt;</p><p><br/></p>','秦林慧','2016-09-13 16:27:37','7','5','./Public/Uploads/2016-09-13/57d7b879ec854.jpg');
